import React from "react";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
const UserProfile = () => {
  // const { user } = useContext(UserContext);

  let sessUser = JSON.parse(localStorage.getItem("sess-user"));

  const navigate = useNavigate();
  return (
    <div className="user-profile-container">
      {sessUser?.id ? (
        <>
          <header className="user-header">
            <div className="text-container">
              <h1>Welcome, {sessUser.name}</h1>
              <p>You've logged in succesfully</p>
              <p>Member since {sessUser.createdAt.split("T")[0]}</p>
            </div>
          </header>
          <div className="favorite-movies">
            <h3>Your favorite movies:</h3>
            <FontAwesomeIcon icon={faTrash} inverse className="" size="2x" />
            <ul>
              {sessUser?.favoriteMovies?.map((movie, i) => {
                return (
                  <li key={i}>
                    <h3>{movie.title}</h3>
                  </li>
                );
              })}
            </ul>
          </div>
        </>
      ) : (
        <div>
          <h1>You have to log your account. </h1>
          <button onClick={() => navigate("/user/login")}>
            Go to Login page
          </button>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
