import React from "react";

const SingleMovie = ({ movies }) => {
  console.log(movies);
  if (!movies) return <p>Loading data...</p>;

  return (
    <>
      {movies.map((movie) => {
        return (
          <div className="cardMovie" key={movie.id}>
            <h1>{movie.title}</h1>
            <img src={movie.backdrop_path} alt="movie image" />
          </div>
        );
      })}
    </>
  );
};

export default SingleMovie;
