import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { faTrashCan } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
const UserProfile = () => {
  // const { user } = useContext(UserContext);

  let sessUser = JSON.parse(localStorage.getItem("sess-user"));

  // console.log(sessUser);
  const deleteAllFavorites = () => {
    delete sessUser.favoriteMovies;
  };

  const [favMovies, setFavMovies] = useState([]);

  useEffect(() => {
    getFavMovies();
  }, []);

  let arr = [];

  const getFavMovies = async () => {
    let results = await Promise.all(
      sessUser.favoriteMovies.map((movieid) => {
        return axios.get(`/movies/singlemovie/${movieid}`).then((obj) => {
          console.log(obj.data);
          return obj.data;
        });
      })
    );
    setFavMovies(results);
  };

  // const getFavMovies = async () => {
  //   await sessUser.favoriteMovies.map((movieid) => {
  //     return axios
  //       .get(`/movies/singlemovie/${movieid}`)
  //       .then((obj) => obj.data)
  //       .then((movie) => {
  //         arr.push(movie);
  //         console.log("arr ", arr);
  //       })
  //       .catch((err) => console.error(err));
  //   });
  //   setFavMovies(arr);
  //   console.log("favmovies", favMovies);
  // };

  const navigate = useNavigate();
  return (
    <div className="user-profile-container">
      {sessUser?.id ? (
        <>
          <header className="user-header">
            <div className="text-container">
              <h1>Welcome, {sessUser.name}</h1>
              <p>You've logged in succesfully</p>
              <p>Member since {sessUser.createdAt.split("T")[0]}</p>
            </div>
          </header>
          <div className="favorite-movies">
            <div className="favorites-title">
              <h3>Your favorite movies:</h3>
              <div onClick={deleteAllFavorites}>
                <FontAwesomeIcon
                  icon={faTrash}
                  inverse
                  className=""
                  size="2x"
                />
              </div>
            </div>
            <ul>
              {/* {sessUser?.favoriteMovies?.map((movie, i) => {
                return (
                  <li className="single-favoriteMovie-container" key={i}>
                    <FontAwesomeIcon icon={faTrashCan} inverse size="1x" />
                    <h3>{movie}</h3>
                  </li>
                );
              })} */}
              {favMovies?.map((movie, i) => {
                return (
                  <li className="single-favoriteMovie-container" key={i}>
                    <FontAwesomeIcon icon={faTrashCan} inverse size="1x" />
                    <h3>{movie.title}</h3>
                    <img
                      src={`https://image.tmdb.org/t/p/w300/${movie.backdrop_path}`}
                      alt="movie favorite"
                    ></img>
                  </li>
                );
              })}

              {arr.map((movie, i) => {
                return (
                  <li className="single-favoriteMovie-container" key={i}>
                    <FontAwesomeIcon icon={faTrashCan} inverse size="1x" />
                    <h3>{movie.title}</h3>
                    <img
                      src={`https://image.tmdb.org/t/p/w300/${movie.backdrop_path}`}
                      alt="movie favorite"
                    ></img>
                  </li>
                );
              })}
            </ul>
          </div>
        </>
      ) : (
        <div>
          <h1>You have to log your account. </h1>
          <button onClick={() => navigate("/user/login")}>
            Go to Login page
          </button>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
