import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
// import axios from "axios";
// import { Link } from "react-router-dom";

const MovieDetails = ({ movie }) => {
  // console.log(movie);
  if (!movie) return <p>Loading data...</p>;

  let sessUser = JSON.parse(localStorage.getItem("sess-user"));

  function SaveDataToLocalStorage(data) {}
  const [favs, setFavs] = useState([]);

  const addToFavorite = () => {
    if (favs) {
      setFavs([...favs, movie]);
    } else {
      setFavs(movie);
    }

    sessUser.favoriteMovies = [favs];
    console.log(sessUser);
    localStorage.setItem("sess-user", JSON.stringify(sessUser));
  };

  return (
    <>
      <div className="singlemovieposter">
        <img
          src={`https://image.tmdb.org/t/p/w300/${movie.backdrop_path}`}
          alt="poster"
        />
      </div>
      <div className="header-poster">
        <div className="title&desc">
          <h1>
            {movie.title}{" "}
            <span className="releaseDate">({movie.release_date})</span>
          </h1>
          <div className="facts">
            <span className="genres">
              {movie.genres ? (
                movie.genres.map((genre, i) => {
                  return (
                    <a href="/#" key={i}>
                      {genre.name},
                    </a>
                  );
                })
              ) : (
                <p>Loading</p>
              )}
            </span>
          </div>
        </div>

        <div className="actions" onClick={addToFavorite}>
          <FontAwesomeIcon icon={faHeart} inverse className="favotireIcon" />
          <a href="/#">add to favotires</a>
        </div>

        <div className="header-info">
          <h3 className="tagline">{movie.tagline}</h3>
          <h2>Overview</h2>
          <p>{movie.overview}</p>
        </div>
      </div>

      <div className="people"></div>
    </>
  );
};

export default MovieDetails;
