import React, { useState, useEffect } from "react";
import CardSingleMovie from "../components/CardSingleMovie";
const axios = require("axios");
const API = "https://api.themoviedb.org/3";
const apiKEY = "api_key=e9e7cb266dc0d3f00bd94a93dae48419";

const Home = () => {
  const [popular, setPopular] = useState({});
  const [top_rated, setTopRated] = useState({});
  const [upcoming, setUpcoming] = useState({});

  useEffect(() => {
    axios
      .get(`${API}/movie/popular?${apiKEY}&language=en-US`)
      .then((response) => {
        setPopular(response.data);
      })
      .catch((err) => console.error(err));
    axios
      .get(`${API}/movie/top_rated?${apiKEY}&language=en-US&page=1`)
      .then((response) => {
        setTopRated(response.data);
      })
      .catch((err) => console.error(err));
    axios
      .get(`${API}/movie/upcoming?${apiKEY}&language=en-US&page=1`)
      .then((response) => {
        setUpcoming(response.data);
      })
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="herohome">
      <h1>Fancy title...</h1>
      <CardSingleMovie movies={popular} />
      <CardSingleMovie movies={top_rated} />
      <CardSingleMovie movies={upcoming} />
    </div>
  );
};

export default Home;
